// Helper to convert JsonObjects to (loan, age) tuples.
def convertToTuple(record: JsonObject) : (String, Option[Long]) = {
  val loan = record.get("Loan_Num_Q").getAsString //.toLowerCase
  val rate = if (record.has("Age_Q")) Some(record.get("Age_Q").getAsLong) else None
  return (loan, rate)
}

// Helper to convert (word, count) tuples to JsonObjects.
//import com.google.gson.JsonNull
def convertToJson(pair: (String, Option[Long])) : JsonObject = {
  val loan = pair._1
  val count = pair._2
  val jsonObject = new JsonObject()
  jsonObject.addProperty("loan_num", loan)
//  if(count != None) jsonObject.addProperty("noterate_count", count.get) else jsonObject.add("noterate_count", JsonNull.INSTANCE)
  count.foreach(jsonObject.addProperty("noterate_count", _))
  return jsonObject
}


